<script type="text/javascript">
	CargarDatosRegion();
	//
	function CargarDatosRegion(){
		$.ajax({
			url: '<?=$base_url?>/informe/datosRegion',
			type: 'POST'
		}).done(function(response){
			if (response.length > 0){
			//arreglos para enviar
				var titulo = [];
				var cantidad = [];

				//
				var data = JSON.parse(response);
				for (var i = 0; i < data.length; i++) {
					titulo.push(data[i]['region']);
					cantidad.push(data[i]['totalR']);
				}
				maquetarGraficas(titulo, cantidad,'doughnut','Quejeas por Región','regiones')
			}

		})
	}

	function maquetarGraficas(titulo, cantidad, tipo, encabezado, id){
		var ctx = document.getElementById(id);
		var myChart = new Chart(ctx, {
		    type: tipo,
		    data: {
		        labels: titulo,
	                      
		        datasets: [{
		            label: encabezado,
		            data: cantidad,
		            backgroundColor: [
		                'rgba(255, 99, 132)',
		                'rgba(54, 162, 235)',
		                'rgba(255, 206, 86)',
		                'rgba(75, 192, 192)',
		                'rgba(153, 102, 255)',
		                'rgba(255, 159, 64)',
		                'rgba(153, 102, 155)',
		                'rgba(255, 159, 14)'
		            ],
		            borderColor: [
		                'rgba(255, 99, 132, 1)',
		                'rgba(54, 162, 235, 1)',
		                'rgba(255, 206, 86, 1)',
		                'rgba(75, 192, 192, 1)',
		                'rgba(153, 102, 255, 1)',
		                'rgba(255, 159, 64, 1)',
		                'rgba(153, 102, 155, 1)',
		                'rgba(255, 159, 14, 1)'
		            ],
		            borderWidth: 1,
                padding: 10,
                datalabels:{
                    color: '#000000',
                    font: {
                        size: 14,
                        weight: 'bold',
                        fontFamily: "'Arial'",
                    },
                    anchor: 'end',
                    align: 'end',
                }
            }]
        },
        options: {
             layout: {
                padding: {
                    left: 0,
                    right: 0,
                    top: 0,
                    bottom: 20
                }
            },
	        legend: {
	            display: true,
	            labels: {
	                fontColor: '#000',
	                fontStyle: 'bold',
		            padding: 20
            	}
        	}
        }
		});
	}

	 $(document).ready(function() {
	    $('#datosqueja').DataTable( {
	        "scrollY": 500,
	        "scrollX": true
	    } );
	} );
</script>
